import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../../services/api"; // Assumindo que você tem um serviço API configurado
import { getUserRole } from "../../utils/auth"; // Função que retorna se o usuário é ADMIN ou USER

const Dashboard: React.FC = () => {
  const navigate = useNavigate();
  const [activeVotes, setActiveVotes] = useState([]);
  const [closedVotes, setClosedVotes] = useState([]);
  const userRole = getUserRole(); // Obtém o papel do usuário

  // Buscar votações ao carregar a página
  useEffect(() => {
    const fetchVotes = async () => {
      try {
        const response = await api.get("/api/votes_session/");
        const votes = response.data

        // Filtra as votações ativas e encerradas
        setActiveVotes(votes.filter((vote: any) => vote.status === "ACTIVE"))
        setClosedVotes(votes.filter((vote: any) => vote.status === "ENDED"))
      } catch (error) {
        console.error("Erro ao buscar votações:", error)
      }
    }
    fetchVotes()
  }, [])

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-bold mb-4">Painel de Votação</h1>

      {/* Botão para Criar Nova Votação - Apenas Admin */}
      {userRole === "ADMIN" && (
        <button
          onClick={() => navigate("/create-vote")}
          className="bg-blue-600 text-white py-2 px-4 rounded-lg mb-4 hover:bg-blue-700"
        >
          Criar Nova Votação
        </button>
      )}

      {/* Votações Ativas */}
      <section className="mb-6">
        <h2 className="text-xl font-semibold mb-2">Votações Ativas</h2>
        {activeVotes.length > 0 ? (
          <ul className="space-y-2">
            {activeVotes.map((vote: any) => (
              <li
                key={vote.id}
                className="p-4 bg-gray-100 rounded-lg flex justify-between"
              >
                <span>{vote.title}</span>
                <button
                  onClick={() => navigate(`/vote/${vote.id}`)}
                  className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700"
                >
                  Votar
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p>Nenhuma votação ativa no momento.</p>
        )}
      </section>

      {/* Votações Encerradas */}
      <section>
        <h2 className="text-xl font-semibold mb-2">Votações Encerradas</h2>
        {closedVotes.length > 0 ? (
          <ul className="space-y-2">
            {closedVotes.map((vote: any) => (
              <li
                key={vote.id}
                className="p-4 bg-gray-200 rounded-lg flex justify-between"
              >
                <span>{vote.title}</span>
                <button
                  onClick={() => navigate(`/results/${vote.id}`)}
                  className="bg-gray-700 text-white px-3 py-1 rounded hover:bg-gray-800"
                >
                  Ver Resultados
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p>Nenhuma votação encerrada.</p>
        )}
      </section>
    </div>
  )
}

export default Dashboard
