import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { userApi } from '../../services/api'
import {
  AuthContainer,
  AuthForm,
  AuthInput,
  AuthButton,
  SwitchAuth
} from '../../styles/AuthStyles'

const Login = () => {
  const navigate = useNavigate()
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const response = await api.post("/users/login", { email, password });
      localStorage.setItem("token", response.data.token);

      // Pega os dados do usuário logado para ver o papel (Admin ou Usuário)
      const userResponse = await api.get("/users/me", {
        headers: { Authorization: `Bearer ${response.data.token}` },
      });

      const userRole = userResponse.data.role;
      localStorage.setItem("role", userRole);

      // Redireciona com base no papel
      if (userRole === "ADMIN") {
        navigate("/create-vote"); // Página de criação de votação
      } else {
        navigate("/dashboard"); // Página de votações ativas
      }
    } catch (error) {
      alert("Erro ao fazer login");
      console.error(error);
    }
  };

  return (
    <AuthContainer>
      <AuthForm onSubmit={handleLogin}>
        <h2>Login</h2>
        <AuthInput
          type="email"
          placeholder="E-mail"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <AuthInput
          type="password"
          placeholder="Senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <AuthButton type="submit">Entrar</AuthButton>
        <SwitchAuth>
          Não tem uma conta?{' '}
          <a onClick={() => navigate('/register')}>Registre-se</a>
        </SwitchAuth>
      </AuthForm>
    </AuthContainer>
  )
}

export default Login
