import { useEffect, useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import { getUserId } from '../../utils/auth'
import api from '../../services/api'

interface VoteSession {
  id: number
  title: string
  options: string[]
  startAt: string
  endAt: string
}

const VotePage = () => {
  const { id } = useParams()
  const navigate = useNavigate()
  const [voteSession, setVoteSession] = useState<VoteSession | null>(null)
  const [selectedOption, setSelectedOption] = useState('')

  useEffect(() => {
    const fetchVoteSession = async () => {
      try {
        const { data } = await api.get(`/api/votes_session/${id}`)
        setVoteSession(data)
      } catch (error) {
        alert('Erro ao carregar votação!')
        navigate('/dashboard')
      }
    }
    fetchVoteSession()
  }, [id, navigate])

  const handleVote = async () => {
    if (!selectedOption) {
      alert('Selecione uma opção!')
      return
    }

    try {
      await api.post(`/api/votes/${id}/cast`, {
        userId: getUserId(),
        option: selectedOption
      })
      alert('Voto registrado com sucesso!')
      navigate('/dashboard')
    } catch (error: any) {
      alert(error.response?.data?.message || 'Erro ao votar!')
    }
  }

  if (!voteSession) return <p>Carregando...</p>

  const now = new Date()
  const startDate = new Date(voteSession.startAt)
  const endDate = new Date(voteSession.endAt)

  if (now < startDate) return <p>A votação ainda não começou.</p>
  if (now > endDate) return <p>A votação já foi encerrada.</p>

  return (
    <div className="p-6 max-w-lg mx-auto bg-white shadow-md rounded-lg">
      <h2 className="text-xl font-bold mb-4">{voteSession.title}</h2>
      {voteSession.options.map((option) => (
        <div key={option} className="flex items-center gap-2 mb-2">
          <input
            type="radio"
            name="vote"
            value={option}
            onChange={() => setSelectedOption(option)}
          />
          <label>{option}</label>
        </div>
      ))}
      <button
        onClick={handleVote}
        className="bg-blue-500 text-white px-4 py-2 rounded"
      >
        Confirmar Voto
      </button>
    </div>
  )
}

export default VotePage
