import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import api from '../../services/api'
import { getUserRole } from '../../utils/auth'

const CreateVote = () => {
  const navigate = useNavigate()
  const [title, setTitle] = useState('')
  const [options, setOptions] = useState([''])
  const [startAt, setStartAt] = useState('')
  const [endAt, setEndAt] = useState('')

  // Verifica se usuário é admin
  if (getUserRole() !== 'ADMIN') {
    return <h2>Acesso Negado. Apenas Admin pode criar votações.</h2>
  }

  const addOption = () => setOptions([...options, ''])
  const removeOption = (index: number) => {
    setOptions(options.filter((_, i) => i !== index))
  }

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    try {
      await api.post('/api/votes_session/create', {
        title,
        options,
        startAt,
        endAt
      })
      alert('Votação criada com sucesso!')
      navigate('/dashboard')
    } catch (error) {
      alert('Erro ao criar votação!')
      console.error(error)
    }
  }

  return (
    <div className="p-6 max-w-lg mx-auto bg-white shadow-md rounded-lg">
      <h2 className="text-xl font-bold mb-4">Criar Nova Votação</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Título da Votação"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="w-full p-2 border rounded mb-2"
        />
        <div>
          <label>Opções de Voto:</label>
          {options.map((option, index) => (
            <div key={index} className="flex gap-2 mb-2">
              <input
                type="text"
                value={option}
                onChange={(e) =>
                  setOptions(
                    options.map((opt, i) =>
                      i === index ? e.target.value : opt
                    )
                  )
                }
                className="w-full p-2 border rounded"
              />
              <button type="button" onClick={() => removeOption(index)}>
                ❌
              </button>
            </div>
          ))}
          <button type="button" onClick={addOption} className="mb-4">
            ➕ Adicionar Opção
          </button>
        </div>
        <input
          type="datetime-local"
          value={startAt}
          onChange={(e) => setStartAt(e.target.value)}
          className="w-full p-2 border rounded mb-2"
        />
        <input
          type="datetime-local"
          value={endAt}
          onChange={(e) => setEndAt(e.target.value)}
          className="w-full p-2 border rounded mb-2"
        />
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          Criar Votação
        </button>
      </form>
    </div>
  )
}

export default CreateVote
